/**
 * Get articles by category
 */
export const getArticlesByCategory = (category) => {
  return articleMetadata.filter(article => article.category === category && article.status !== 'planned');
};

/**
 * Get articles by sector
 */
export const getArticlesBySector = (sector) => {
  return articleMetadata.filter(article =>
    article.sector.includes(sector) && article.status !== 'planned'
  );
};

/**
 * Get articles by standard
 */
export const getArticlesByStandard = (standard) => {
  return articleMetadata.filter(article =>
    article.standards.includes(standard) && article.status !== 'planned'
  );
};

/**
 * Get featured articles
 */
export const getFeaturedArticles = () => {
  return articleMetadata.filter(article => article.featured && article.status !== 'planned');
};

/**
 * Get all published articles (exclude planned)
 */
export const getPublishedArticles = () => {
  return articleMetadata.filter(article => article.status !== 'planned');
};

/**
 * Get article by slug
 */
export const getArticleBySlug = (slug) => {
  return articleMetadata.find(article => article.slug === slug);
};

/**
 * Get unique sectors
 */
export const getAllSectors = () => {
  const sectors = new Set();
  articleMetadata.forEach(article => {
    article.sector.forEach(s => sectors.add(s));
  });
  return Array.from(sectors).sort();
};

/**
 * Get unique standards
 */
export const getAllStandards = () => {
  const standards = new Set();
  articleMetadata.forEach(article => {
    article.standards.forEach(s => standards.add(s));
  });
  return Array.from(standards).sort();
};

/**
 * Get unique types
 */
export const getAllTypes = () => {
  const types = new Set();
  articleMetadata.forEach(article => {
    types.add(article.type);
  });
  return Array.from(types).sort();
};

/**
 * Search articles by query
 */
export const searchArticles = (query) => {
  const lowerQuery = query.toLowerCase();
  return articleMetadata.filter(article =>
    article.status !== 'planned' && (
      article.title.toLowerCase().includes(lowerQuery) ||
      article.abstract.toLowerCase().includes(lowerQuery) ||
      article.tags.some(tag => tag.toLowerCase().includes(lowerQuery))
    )
  );
};

export default articleMetadata;
